function Square({value}) {
  return <button class="square">{value}</button>;
}

function Row({rowId}) {
  var id = "rowId"+rowId;

  return <div id={id} class="board-row"></div>;
}

export default function Board() {
  return (
    <>
      <Row rowId='1'>
        <Square value='1'/>
        <Square value='2'/>
        <Square value='3'/>
      </Row>
      <Row rowId='2'>
        <Square value={1}/>
        <Square />
        <Square />
      </Row>
      <Row rowId='3'>
        <Square value={1}/>
        <Square />
        <Square />
      </Row>
    </>
  );
  // return <BuildBoard dimensions={3}/>;
}




