import { useState } from 'react';


function Square({value}) {
  function handleClick(){
    // console.log("clicked on button with value of  "+ value);
    value = "X";
  }

  return (
    <button 
      class="square"
      onClick={handleClick}
    >
      {value}
    </button>
  );
}

function Row({rowId}) {
  var id = "rowId"+rowId;

  return <div id={id} class="board-row"></div>;
}

