function Square({id}) {
  return <button class="square">{id}</button>;
}


export default function Board() {
  return (
    <>
      <Row rowId='3'>
        <Square />
        <Square />
        <Square />
      </Row>
      <div className="board-row">
        <Square />
        <Square />
        <Square />
      </div>
      <div className="board-row">
        <Square />
        <Square />
        <Square />
      </div>
    </>
  );
  // return <BuildBoard dimensions={3}/>;
}

function Row({rowId}) {
  var id = "rowId"+rowId;

  return(
    <div id={id} class="board-row"></div>
  );
}


//builds the squares of the board
function BuildBoard(dimensions) {
  //3 columns of  3 rows of buttons. 
  var board = null;


  //iterate through rows
  for (let index = 0; index < dimensions+1; index++) {
    var rowId = "row"+index;
    board += <Row id={index}/>
    
    //iterate through columns
    for (let index = 0; index < dimensions+1; index++){
      var row = document.getElementById("rowId"+index);
      row += <Square id='1'/>
    }
    
  }

  return board;
}
