function Square() {
  const [value, setValue] = useState(null); 
  const [chungus, setChungus] = useState(null);
  
  function handleClick(){
    // console.log("clicked on button with value of  "+ value);
    setValue("X");
    setChungus("chung");
  }

  return (
    <button 
      class="square"
      onClick={handleClick}
    >
    </button>
  );
}

function Row({rowId}) {
  var id = "rowId"+rowId;

